    private void btn_sesionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_sesionActionPerformed
        EstudianteController ec = new EstudianteController();
        String pass = new String(jPF_Password.getPassword());
        String usuario = jTF_Usuario.getText();
        btn_sesion.transferFocus();
        if(jTF_Usuario.getText().equals("") || pass.equals("")){
            JOptionPane.showMessageDialog(null, "No puede quedar campos vacios al iniciar sesion.");
        }else{           
            if(ec.existeUsuario(usuario)){
                String password = new String(jPF_Password.getPassword());
                String nom=ec.buscarNombre(usuario);
                if(ec.existePassword(usuario, password)){
                        user = ec.buscarDatosEstudiante(usuario);
                        Principal p = new Principal();
                        p.setVisible(true);
                        this.dispose();
                }else{
                    JOptionPane.showMessageDialog(null, "Usuario y/o contrase침a incorrecta.");
                }
            }else{
                JOptionPane.showMessageDialog(null, "Usuario y/o contrase침a incorrecta.");
            }
            
        }
    }//GEN-LAST:event_btn_sesionActionPerformed

